import React from 'react'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
// import img1 from "../img/Tesla.jpeg";
// import img2 from "../img/Renault.jpeg";
// import img3 from "../img/samsung.jpeg";
// import img4 from "../img/uilyam.jpeg";
// import img5 from "../img/wyze.jpeg";
// import img6 from "../img/google4a.jpeg"
import Data from '../data'


const SlideSection = () => {

  const slideData = Data.slide

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    // className: "center",
    centerPadding: "0px",
    centerMode: true,
    variableWidth: true,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerPadding: "0px",
          infinite: true,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerPadding: "0px",
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerPadding: "0px",
        }
      }
    ]
  };
  return (
    <section  className="section-3 section">
      <div className="container">
        <div className="container-fluid">
          <div className="row">
          <div className="col-md-12">
            <div className="news">
              <h2>Xəbərlər</h2>
            </div>
          </div>
          <div className="col-md-12">
            <div className="wrapper">

            <Slider {...settings}>

              {
                slideData.map((slide,i) =>{
                  const {img,text} = slide
                  return(
                    <div key={i} className="product-div">
                      <img src={img} alt=""/>
                      <p> {text} </p>
                        <button className="js-button" >
                          <a  href="./news/blog.html" target="_self" >
                            Daxil ol
                          </a>
                        </button>
                    </div> 
                  )
                })
              }

            </Slider>

            </div>
          </div>
          </div>
        </div>
      </div>
  </section>
  )
}

export default SlideSection
