import React,{useRef,useState} from 'react'
import functions from './functions';
import toast from 'react-hot-toast';

const ContactSection = ({myRef}) => {


  const [contactData, setContactData] = useState({
    name:"",
    email:"",
    phone:"",
    message: ""
  })
  const [email, setEmail] = useState("")

  if(contactData.name){
    console.log(true)
  }else{
    console.log(false)
  }



  const submitHandler = () =>{
    let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    if(re.test(email)){
      if( contactData.name){
        setContactData({
          email: email
        })
      const xhttp = new XMLHttpRequest();
      // xhttp.open("POST", "https://infinite-castle-76696.herokuapp.com/send");
      xhttp.open("POST", "https://infinite-castle-76696.herokuapp.com/send");
      xhttp.setRequestHeader("Content-type", 'application/json');
      xhttp.send(JSON.stringify(contactData));
      setContactData({
        name:"",
        email:"",
        phone:"",
        message: ""
      })
      toast('Mesaj göndərildi');
    }else{
      alert('xaiş edirik xanaların hamısını doldurun')
    }
    }else{
      alert("Emaili doğru yazın")
    }
  }


  return (
  <section ref={myRef} id="Əlaqə" className="section-4 " >
    <div className="container"  >
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <div className="contact-text">
              <h3>Əlaqə</h3>
            </div>
          </div>
          <div className="col-md-12">
            <div className="form-div">
            <form action="">
              <div className="three-div">
                <label htmlFor="name"> Ad:
                  <input id="name"   type="text" onChange={(e) => setContactData({
                    name:e.target.value
                  })}  required />
                </label>
                <label htmlFor="Email"> Email:
                  <input id="email" type="email" onChange={(e) => setEmail(e.target.value)} required />
                </label>
                <label htmlFor="Phone" > Telefon:
                  <input id="phone" type="number" onChange={(e) => setContactData({
                    phone:e.target.value
                  })} required />
                </label>
              </div>
              <label htmlFor="message"> Mesaj:
                <textarea id="message" onChange={(e) => setContactData({
                    message:e.target.value
                  })} name="text" ></textarea>
              </label>

            </form>
            <button onClick={() => submitHandler()} id="btn">Təsdiq</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
  )
}

export default ContactSection
